  // const [todo, setTodo] = useState();
  // Outro state será a tarefa atual do input que será puxada para o list ([...list, todo])

  // Função trim é para tirar os ESPAÇOS
  return (
    <div className="container">

      <h1>Lista de Tarefas</h1>
      <div className="miniContainer">
        <input type="text" />
        <button type="submit" className="plus">
          <BsPlusSquare className="svg" />
        </button>
      </div>

    </div>
  );
}

