import React, { useState } from 'react';
import { BsPlusSquare } from 'react-icons/bs';

export default function Main() {
  const [list, setList] = useState(['Fazer comida', 'Ir ao mercado']);
  // Um state será o conjunto de tarefas
  const [todo, setTodo] = useState();
  // Outro state será a tarefa atual do input que será puxada para o list ([...list, todo])

  // Função trim é para tirar os ESPAÇOS
  return (

    <div className="container">
      {todo}
      <h1>Lista de Tarefas</h1>
      <div className="miniContainer">
        <input
          type="text"
          onChange={(e) => setTodo(e.target.value)}
        />
        <button type="submit" className="plus">
          <BsPlusSquare className="svg" />
        </button>
        {list?.map((li) => (
          <ul>
            <li>{li}</li>
          </ul>
        ))}
      </div>

    </div>
  );
}

