import React, { useState } from 'react';
import { BsPlusSquare, BsPen } from 'react-icons/bs';
import { AiOutlineDelete } from 'react-icons/ai';

export default function Main() {
  const [list, setList] = useState([]);
  const [todo, setTodo] = useState({
    title: '',
    isComplete: false,
    update: -1,
  });

  // Para deletar eu copio a lista. Pego o elemento do array e adisiono o método splice.
  const handleDeleteTodo = (index) => {
    const copyList = [...list];
    copyList.splice(index, 1);
    setList(copyList);
  };

  // Adiociono o item. Caso tenha espaço vazio eu não adiciono a lista,
  // senão eu copio a lista e adiciono uma tarefa.

  const handleChange = () => {
    if ((todo.title).trim() === '') {
      return;
    }
    if (todo.update === -1) {
      setList([...list, todo]);
      setTodo({
        title: '',
        isComplete: false,
        update: -1,
      });
    } else {
      const copyList = [...list];
    }
  };

  // Para checar se já foi lido - copio a lista, pego o item do array e mudo o .isComplete
  const handleCheckTodo = (index) => {
    const copyList = [...list];
    copyList[index].isComplete = !copyList[index].isComplete;
    setList(copyList);
  };

  // Para editar
  const handleUpdate = (index) => {
    const copyList = [...list];
    setTodo({
      title: copyList[index].title,
      isComplete: false,
      update: index,
    });
  };

  return (

    <form onSubmit={(e) => e.preventDefault()} className="container">
      <h1>Lista de Tarefas</h1>
      <div className="miniContainer">
        <input
          type="text"
          value={todo.title}
          onChange={(e) => setTodo({ title: e.target.value, isComplete: false })}
        />
        <button
          type="submit"
          className="plus"
          onClick={handleChange}
        >
          <BsPlusSquare className="svg" />
        </button>

      </div>
      <ul>
        {list?.map((li, index) => (

          <li key={li}>
            <div>
              <input
                type="checkbox"
                onClick={() => handleCheckTodo(index)}
              />
              {li.isComplete === false ? li.title : <s>{li.title}</s>}
            </div>
            <div>
              <BsPen onClick={() => handleUpdate(index)} />
              <AiOutlineDelete onClick={() => handleDeleteTodo(index)} />
            </div>
          </li>

        ))}
      </ul>
    </form>
  );
}

