import React, { useState } from 'react';
import { BsPlusSquare, BsPen } from 'react-icons/bs';
import { AiOutlineDelete } from 'react-icons/ai';

export default function Main() {
  const [list, setList] = useState(['Fazer comida', 'Ir ao mercado']);
  const [todo, setTodo] = useState({
    title: '',
    isComplete: false,
  });
  // const [check, setCheck] = useState(false);

  const handleDeleteTodo = (index) => {
    const copyList = [...list];
    copyList.splice(index, 1);
    setList(copyList);
  };
  const handleChange = () => {
    if ((todo.title).trim() === '') {
      return;
    }

    setList([...list, todo]);
    setTodo({
      title: '',
      isComplete: false,
    });
  };

  // const handleCheckTodo = (index) => {
  //   this.checked ? j : key;
  // };

  return (

    <form onSubmit={(e) => e.preventDefault()} className="container">
      <h1>Lista de Tarefas</h1>
      <div className="miniContainer">
        <input
          type="text"
          value={todo.title}
          onChange={(e) => setTodo({ title: e.target.value, isComplete: false })}
        />
        <button
          type="submit"
          className="plus"
          onClick={handleChange}
        >
          <BsPlusSquare className="svg" />
        </button>

      </div>
      <ul>
        {list?.map((li, index) => (

          <li key={li}>
            <div>
              <input
                type="checkbox"
              />
              {li.isComplete === false ? li.title : <s>{li.title}</s>}

            </div>
            <div>
              <BsPen />
              <AiOutlineDelete onClick={() => handleDeleteTodo(index)} />
            </div>
          </li>

        ))}
      </ul>
    </form>
  );
}

