import React, { useState } from 'react';
import { BsPlusSquare, BsPen } from 'react-icons/bs';
import { AiOutlineDelete } from 'react-icons/ai';

export default function Main() {
  const [list, setList] = useState(['Fazer comida', 'Ir ao mercado']);
  const [todo, setTodo] = useState();
  const [check, setCheck] = useState(false);

  const handleDeleteTodo = (index) => {
    const newList = [...list].splice(index, 1);
    setList(newList);
  };
  // const handleCheckTodo = ()=>{

  // }
  const handleChange = () => {
    if (todo.trim() === '') {
      return;
    }
    setList([...list, todo]);
    setTodo('');
  };
  // Função trim é para tirar os ESPAÇOS
  return (

    <form onSubmit={(e) => e.preventDefault()} className="container">
      <h1>Lista de Tarefas</h1>
      <div className="miniContainer">
        <input
          type="text"
          value={todo}
          onChange={(e) => setTodo(e.target.value)}
        />
        <button
          type="submit"
          className="plus"
          onClick={handleChange}
        >
          <BsPlusSquare className="svg" />
        </button>

      </div>
      <ul>
        {list?.map((li, index) => (

          <li key={li}>
            <div>
              <input
                type="checkbox"
                // onClick={() => handleCheck(index)}
              />
              {console.log(check)}
              {check === true ? <s>{li}</s> : li }
            </div>
            <div>
              <BsPen />
              <AiOutlineDelete onClick={() => handleDeleteTodo(index)} />
            </div>
          </li>

        ))}
      </ul>
    </form>
  );
}

